source("main_func.R")

bittrex_func <- function(finish_table, lim_or_mark, data_market_price){
  # заполнение курсов binance
  get_price_bittrex <- function(Name_crypt1, Name_crypt2, Name_crypt3, table_Crypto, lim_or_mark, data_market_price){
    # преобразуем list в data.frame
    data <- as.data.frame(do.call(rbind, data_market_price[["result"]]))
    
    switch(lim_or_mark, 
           lim={
             # заполняем строчку курсов для limit price
             # при прямой --------------------------------------------------
             table_Crypto[1,2] <- as.numeric(data[data[,1]==Name_crypt1,"Bid"])
             table_Crypto[1,3] <- as.numeric(data[data[,1]==Name_crypt2,"Bid"])
             table_Crypto[1,4] <- 1/(as.numeric(data[data[,1]==Name_crypt3,"Ask"]))
             table_Crypto[1,10] <- 1/(as.numeric(data[data[,1]==Name_crypt1,"Ask"]))
             table_Crypto[1,11] <- as.numeric(data[data[,1]==Name_crypt3,"Bid"])
             table_Crypto[1,12] <- 1/(as.numeric(data[data[,1]==Name_crypt2,"Ask"]))
             # при обратной ------------------------------------------------
             table_Crypto[1,6] <- as.numeric(data[data[,1]==Name_crypt3,"Bid"])
             table_Crypto[1,7] <- 1/(as.numeric(data[data[,1]==Name_crypt2,"Ask"]))
             table_Crypto[1,8] <- 1/(as.numeric(data[data[,1]==Name_crypt1,"Ask"]))
             table_Crypto[1,14] <- as.numeric(data[data[,1]==Name_crypt2,"Bid"])
             table_Crypto[1,15] <- 1/(as.numeric(data[data[,1]==Name_crypt3,"Ask"]))
             table_Crypto[1,16] <- as.numeric(data[data[,1]==Name_crypt1,"Bid"])
           },
           mark={
             # заполняем строчку курсов для market price
             # при прямой --------------------------------------------------
             table_Crypto[1,2] <- as.numeric(data[data[,1]==Name_crypt1,"Last"])
             table_Crypto[1,3] <- as.numeric(data[data[,1]==Name_crypt2,"Last"])
             table_Crypto[1,4] <- 1/(as.numeric(data[data[,1]==Name_crypt3,"Last"]))
             table_Crypto[1,10] <- 1/(as.numeric(data[data[,1]==Name_crypt1,"Last"]))
             table_Crypto[1,11] <- as.numeric(data[data[,1]==Name_crypt3,"Last"])
             table_Crypto[1,12] <- 1/(as.numeric(data[data[,1]==Name_crypt2,"Last"]))
             # при обратной ------------------------------------------------
             table_Crypto[1,6] <- as.numeric(data[data[,1]==Name_crypt3,"Last"])
             table_Crypto[1,7] <- 1/(as.numeric(data[data[,1]==Name_crypt2,"Last"]))
             table_Crypto[1,8] <- 1/(as.numeric(data[data[,1]==Name_crypt1,"Last"]))
             table_Crypto[1,14] <- as.numeric(data[data[,1]==Name_crypt2,"Last"])
             table_Crypto[1,15] <- 1/(as.numeric(data[data[,1]==Name_crypt3,"Last"]))
             table_Crypto[1,16] <- as.numeric(data[data[,1]==Name_crypt1,"Last"])
           })
    # ------------------
    return(table_Crypto)
  }
  
  # -----------------------------------------------------------------------------------
  # узнать номер криптовалютной пары для первых 3 параметров в нижних функциях
  #what_num_crypt<-as.data.frame.raw(data_market_price[["result"]])
  #View(what_num_crypt)
  
  # заполняем значение курса
  table_ADA_USDT <- get_price_bittrex("BTC-ADA", "USDT-BTC", "USDT-ADA", table_ADA_USDT, lim_or_mark, data_market_price)
  table_ADA_ETH <- get_price_bittrex("ETH-ADA", "BTC-ETH", "BTC-ADA", table_ADA_ETH, lim_or_mark, data_market_price)
  table_BCH_USDT <- get_price_bittrex("BTC-BCH", "USDT-BTC", "USDT-BCH", table_BCH_USDT, lim_or_mark, data_market_price)
  table_BCH_ETH <- get_price_bittrex("ETH-BCH", "BTC-ETH", "BTC-BCH", table_BCH_ETH, lim_or_mark, data_market_price)
  table_DASH_USDT <- get_price_bittrex("BTC-DASH", "USDT-BTC", "USDT-DASH", table_DASH_USDT, lim_or_mark, data_market_price)
  table_DASH_ETH <- get_price_bittrex("ETH-DASH", "BTC-ETH", "BTC-DASH", table_DASH_ETH, lim_or_mark, data_market_price)
  table_DCR_USDT <- get_price_bittrex("BTC-DCR", "USDT-BTC", "USDT-DCR", table_DCR_USDT, lim_or_mark, data_market_price)
  table_DGB_USDT <- get_price_bittrex("BTC-DGB", "USDT-BTC", "USDT-DGB", table_DGB_USDT, lim_or_mark, data_market_price)
  table_DGB_ETH <- get_price_bittrex("ETH-DGB", "BTC-ETH", "BTC-DGB", table_DGB_ETH, lim_or_mark, data_market_price)
