  table_ETC_ETH <- get_price_bittrex("ETH-ETC", "BTC-ETH", "BTC-ETC", table_ETC_ETH, lim_or_mark, data_market_price)
  table_ETH_USDT <- get_price_bittrex("BTC-ETH", "USDT-BTC", "USDT-ETH", table_ETH_USDT, lim_or_mark, data_market_price)
  table_GNT_ETH <- get_price_bittrex("ETH-GNT", "BTC-ETH", "BTC-GNT", table_GNT_ETH, lim_or_mark, data_market_price)
