  table_OMG_ETH <- get_price_bittrex("ETH-OMG", "BTC-ETH", "BTC-OMG", table_OMG_ETH, lim_or_mark, data_market_price)
  table_QTUM_ETH <- get_price_bittrex("ETH-QTUM", "BTC-ETH", "BTC-QTUM", table_QTUM_ETH, lim_or_mark, data_market_price)
  table_SNT_ETH <- get_price_bittrex("ETH-SNT", "BTC-ETH", "BTC-SNT", table_SNT_ETH, lim_or_mark, data_market_price)
  table_STRAT_ETH <- get_price_bittrex("ETH-STRAT", "BTC-ETH", "BTC-STRAT", table_STRAT_ETH, lim_or_mark, data_market_price)
  table_SC_USDT <- get_price_bittrex("BTC-SC", "USDT-BTC", "USDT-SC", table_SC_USDT, lim_or_mark, data_market_price)
  table_SC_ETH <- get_price_bittrex("ETH-SC", "BTC-ETH", "BTC-SC", table_SC_ETH, lim_or_mark, data_market_price)
  table_TRX_USDT <- get_price_bittrex("BTC-TRX", "USDT-BTC", "USDT-TRX", table_TRX_USDT, lim_or_mark, data_market_price)
  table_TRX_ETH <- get_price_bittrex("ETH-TRX", "BTC-ETH", "BTC-TRX", table_TRX_ETH, lim_or_mark, data_market_price)
