  table_XMR_ETH <- get_price_bittrex("ETH-XMR", "BTC-ETH", "BTC-XMR", table_XMR_ETH, lim_or_mark, data_market_price)
  table_XRP_USDT <- get_price_bittrex("BTC-XRP", "USDT-BTC", "USDT-XRP", table_XRP_USDT, lim_or_mark, data_market_price)
  table_XRP_ETH <- get_price_bittrex("ETH-XRP", "BTC-ETH", "BTC-XRP", table_XRP_ETH, lim_or_mark, data_market_price)
  table_XVG_USDT <- get_price_bittrex("BTC-XVG", "USDT-BTC", "USDT-XVG", table_XVG_USDT, lim_or_mark, data_market_price)
  table_ZEC_USDT <- get_price_bittrex("BTC-ZEC", "USDT-BTC", "USDT-ZEC", table_ZEC_USDT, lim_or_mark, data_market_price)
  table_ZEC_ETH <- get_price_bittrex("ETH-ZEC", "BTC-ETH", "BTC-ZEC", table_ZEC_ETH, lim_or_mark, data_market_price)
  table_ZRX_ETH <- get_price_bittrex("ETH-ZRX", "BTC-ETH", "BTC-ZRX", table_ZRX_ETH, lim_or_mark, data_market_price)
  
  # -----------------------------------------------------------------------------------
  # выполняем расчет и заполняем строчки
