  table_SC_ETH <- find_balance(table_SC_ETH, SC, ETH, bittrex_fee, 8, 8, 8, 8, 8, 8)
  table_TRX_USDT <- find_balance(table_TRX_USDT, TRX, BTC, bittrex_fee, 8, 8, 8, 8, 8, 8)
  table_TRX_ETH <- find_balance(table_TRX_ETH, TRX, ETH, bittrex_fee, 8, 8, 8, 8, 8, 8)
  table_WAVES_ETH <- find_balance(table_WAVES_ETH, WAVES, ETH, bittrex_fee, 8, 8, 8, 8, 8, 8)
  table_WAX_ETH <- find_balance(table_WAX_ETH, WAX, ETH, bittrex_fee, 8, 8, 8, 8, 8, 8)
  table_XEM_ETH <- find_balance(table_XEM_ETH, XEM, ETH, bittrex_fee, 8, 8, 8, 8, 8, 8)
  table_XLM_ETH <- find_balance(table_XLM_ETH, XLM, ETH, bittrex_fee, 8, 8, 8, 8, 8, 8)
